package ch.bbw.zork;/** * Author:  Michael Kolling, Version: 1.1, Date: August 2000 * refactoring: Rinaldo Lanza, September 2020 */import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.stream.Collectors;public class Room {		private String description;	private HashMap<String, Room> exits;	private Item item;	public Room(String description, Item item) {		this.description = description;		this.exits = new HashMap<>();		this.item = item;	}	public void setExits(Room north, Room east, Room south, Room west) {		exits.put("north", north);		exits.put("east", east);		exits.put("south", south);		exits.put("west", west);	}	public String shortDescription() {		return description;	}	public String longDescription() {		StringBuilder stringBuilder = new StringBuilder("You are in " + description + ".\n");		if (!itemDescription().equals("")) {			stringBuilder.append(exitString() + ".\n");			stringBuilder.append(itemDescription());		} else {			stringBuilder.append(exitString() + ".");		}		return stringBuilder.toString();	}	private String exitString() {		return "Exits:" + String.join(" ", exits.keySet());	}	private String itemDescription() {		if (item.getItemDescription().equals("")) {			return "";		}else {			String roomItem = "You found " + item.getItemDescription() + " in " + description + ".";			return roomItem;		}	}	public Room nextRoom(String direction) {		return exits.get(direction);	}	public void win() {		if (description.equals("on top of some snowy Hills")) {			System.out.println("Congrats, you won");			System.exit(0);		}	}}