package ch.bbw.zork;/** * Author:  Michael Kolling, Version: 1.1, Date: August 2000 * refactoring: Rinaldo Lanza, September 2020 */import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.stream.Collectors;public class Room {    private String name;    private String description;    private HashMap<String, Room> exits;    private ArrayList<Item> item;    public Room(String name, String description) {        this.name = name;        this.description = description;        this.exits = new HashMap<>();        this.item = new ArrayList<>();    }    public String getName() {        return name;    }    public void setExits(Room north, Room east, Room south, Room west) {        exits.put("north", north);        exits.put("east", east);        exits.put("south", south);        exits.put("west", west);    }    public void addItems(Item _item) {        item.add(_item);    }    public String shortDescription() {        return description;    }    public String longDescription() {        StringBuilder stringBuilder = new StringBuilder("You are in " + description + ".\n");        if (!itemDescription().equals("")) {            stringBuilder.append(exitString() + ".\n");            stringBuilder.append(itemDescription());        } else {            stringBuilder.append(exitString() + ".");        }        return stringBuilder.toString();    }    public String exitString() {        return "Exits:" + String.join(" ", exits.keySet());    }    private String itemDescription() {        int num = 0;        StringBuilder stringBuilder = new StringBuilder("You found ");        String descriptionOutput = "";        while (item.size() > num) {            String itemOutput = item.get(num).getItemDescription();            if (item.size() > 1) {                if (itemOutput.equals("")) {                    if (num == (item.size() - 1)) {                        stringBuilder.append(itemOutput + " in " + description + ".");                    }                } else {                    if (num == (item.size() - 1)) {                        stringBuilder.append(itemOutput + " in " + description + ".");                    } else {                        stringBuilder.append(itemOutput + " and ");                    }                }            } else {                if (itemOutput.equals("")) {                    descriptionOutput = itemOutput;                } else {                    descriptionOutput = "You found " + itemOutput + " in " + description + ".";                }            }            num++;        }        if (!stringBuilder.toString().equals("You found ")) {            descriptionOutput = stringBuilder.toString();            return descriptionOutput;        }        return descriptionOutput;    }    public Room nextRoom(String direction) {        return exits.get(direction);    }}